mapper.select:
SELECT id AS no,
	   title,
	   area_code AS areaCode,
	   si_gun_gu_code AS siGunGuCode,
	   latitude,
	   longitude,
	   first_image1 AS FirstImage1,
	   addr1
FROM materialized_union_attractions


		AND area_code = #{areaCode}


		AND title LIKE CONCAT('%', #{keyword}, '%')


		AND si_gun_gu_code = #{siGunGuCode}


		AND content_type_id = #{type}


		AND id > #{cursor}


ORDER BY no ASC
LIMIT #{size}

SELECT id
FROM materialized_union_attractions


		AND title = #{title}

SELECT id AS no, title, area_code AS areaCode, si_gun_gu_code AS siGunGuCode, latitude, longitude, first_image1 AS FirstImage1, addr1 FROM materialized_union_attractions
AND title = #{title}
AND addr1 = #{address}

SELECT id as no, title, first_image1 as FirstImage1, addr1, area_code as areaCode, si_gun_gu_code as siGunGuCode, latitude, longitude
FROM materialized_union_attractions
WHERE id = #{id}
