mapper.select:
select title, area_code as areaCode, si_gun_gu_code as siGunGuCode, latitude, longitude from custom_attractions
AND area_code = #{areaCode}
AND title LIKE CONCAT('%', #{keyword}, '%')
AND si_gun_gu_code = #{siGunGuCode}

select title, area_code as areaCode, si_gun_gu_code as siGunGuCode,
latitude, longitude
from
custom_attractions


		AND title = #{title}


		AND area_code = #{areaCode}


		AND si_gun_gu_code = #{siGunGuCode}
