mapper.select:
select id, title, start_date as startDate, end_date as endDate,
memo, created_at as createdAt, updated_at as updatedAt
from plans
WHERE email = #{email}
ORDER BY updated_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT version
FROM plans
WHERE id = #{id}

mapper.update:
UPDATE plans
SET title   = #{title},
memo    = #{memo},
version = version + 1
WHERE id      = #{id}
AND version = #{version}
